import { USER_REGISTERED } from "../action/types";

const initialState = {
  name: "",

  phone: "",
  friends: [],
  username: "",
};
export const userReducer = (state = initialState, action) => {
  switch (action.type) {
    case USER_REGISTERED:
      return (state = {
        ...action.payload,
      });
    default:
      return state;
  }
};
