import React, { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import { search_reducer } from "../redux/action/searchAction";

const SearchResult = () => {
  const token = localStorage.getItem("token");
  const store = useSelector((data) => data);
  const dispatch = useDispatch();
  const myId = store.userReducer.userId;

  const handleClick = () => {
    setMessage(store.searchReducer.searchFriendId);
  };

  async function setMessage(id) {
    const body = JSON.stringify({
      friendId: id,
    });

    const resp = await fetch(
      "https://telegram-alisherjon-api.herokuapp.com/chats/",
      {
        headers: {
          "Content-Type": "application/json",
          authorization: `Bearer ${token}`,
        },
        method: "POST",
        body,
      }
    );

    const respData = await resp.json();

    console.log(respData);

    if (respData.status === 201) {
      return console.log("Chat is already exists!");
    }

    const memberFriendId = "";

    respData.chat.members.forEach((member) => {
      if (member !== myId) {
        memberFriendId = member;
      }
    });

    const newFriendObj = {
      chatId: respData.chat._id,
      friendName: "Fayzulloh",
      friendPhone: "999015507",
      friendUserName: "Killer",
    };

    const obj = {
      searchFriendId: "",
      searchName: "",
      phone: "",
      isHidden: true,
    };
    dispatch(search_reducer(obj));
  }

  async function getUserInfo(token) {
    const resp = await fetch(
      "https://telegram-alisherjon-api.herokuapp.com/users/",
      {
        headers: {
          "Content-Type": "application/json",
          authorization: `Bearer ${token}`,
        },
        method: "GET",
      }
    );

    const respData = await resp.json();

    console.log(respData);

    const userId = respData.user._id;

    const friends = [];

    respData.user.chats.forEach((member) => {
      const obj = {
        chatId: member._id,
        friendName: "",
        friendPhone: "",
        friendUserName: "",
      };
      member.members.forEach((inside) => {
        if (inside._id !== userId) {
          obj.friendName = inside.name;
          obj.friendPhone = inside.phone;
          obj.friendUserName = inside.username;
        }
      });
      friends.push(obj);
    });

    const user = {
      name: respData.user.name,
      phone: respData.user.phone,
      friends,
      username: respData.user.username,
      userId,
    };

    console.log(user);
  }

  return (
    <div
      onClick={() => handleClick()}
      className={
        store.searchReducer.isHidden ? "search-result hidden" : "search-result "
      }
    >
      <h3>{store.searchReducer.searchName}</h3>
      <h3>{store.searchReducer.phone}</h3>
    </div>
  );
};

export default SearchResult;
