import React from "react";

const Alert = ({ type, message }) => {
  if (type === "success") {
    return (
      <div className="alert alert-success">
        <p>{message}</p>
      </div>
    );
  } else if (type === "danger") {
    return (
      <div className="alert alert-danger">
        <p>{message}</p>
      </div>
    );
  } else if (type === "warning") {
    return (
      <div className="alert alert-danger">
        <p>{message}</p>
      </div>
    );
  } else return null;
};

export default Alert;
