import { USER_MESSAGE } from "../action/types";

const initialState = {
  messages: [],
};

export const messageReducer = (state = initialState, action) => {
  switch (action.type) {
    case USER_MESSAGE:
      return (state = [...action.payload]);
    default:
      return state;
  }
};
