import React, { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";

const SearchResult = () => {
  const token = localStorage.getItem("token");
  const store = useSelector((data) => data);
  const dispatch = useDispatch;

  const handleClick = () => {
    setMessage(store.searchReducer.searchFriendId);
  };

  async function setMessage(id) {
    const body = JSON.stringify({
      friendId: id,
    });

    const resp = await fetch(
      "https://telegram-alisherjon-api.herokuapp.com/chats/",
      {
        headers: {
          "Content-Type": "application/json",
          authorization: `Bearer ${token}`,
        },
        method: "POST",
        body,
      }
    );

    const respData = await resp.json();

    const obj = {
      searchFriendId: "",
      searchName: "",
      phone: "",
      isHidden: true,
    };
    dispatch(search_reducer(obj));
  }

  return (
    <div
      onClick={() => handleClick()}
      className={
        store.searchReducer.isHidden ? "search-result hidden" : "search-result "
      }
    >
      <h3>{store.searchReducer.searchName}</h3>
      <h3>{store.searchReducer.phone}</h3>
    </div>
  );
};

export default SearchResult;
