import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { useStore } from "react-redux";
import Chats from "./ChatMembers";
import SearchResult from "./SearchResult";

const Search = () => {
  const [input, setInput] = useState("");
  const token = localStorage.getItem("token");
  const dispatch = useDispatch();

  const handleSubmit = (e) => {
    e.preventDefault();

    if (input === "") {
      return;
    }

    fetch(`https://telegram-alisherjon-api.herokuapp.com/users/${input}`, {
      headers: {
        "Content-Type": "application/json",
        authorization: `Bearer ${token}`,
      },
    })
      .then((res) => {
        if (!res.ok) {
          return;
        }
        return res.json();
      })
      .then((data) => {
        const obj = {
          searchFriendId: data.user._id,
          searchName: data.user.name,
          phone: data.user.phone,
          isHidden: false,
        };
        console.log(obj);
      });
  };

  async function setChat(id) {
    const body = JSON.stringify({
      friendId: id,
    });

    const resp = await fetch(
      "https://telegram-alisherjon-api.herokuapp.com/chats/",
      {
        headers: {
          "Content-Type": "application/json",
          authorization: `Bearer ${token}`,
        },
        method: "POST",
        body,
      }
    );
    console.log(resp);
    const respData = await resp.json();
    console.log(respData);
    return respData.chat._id;
  }

  return (
    <form onSubmit={(e) => handleSubmit(e)} className="search">
      <div className="menu-icon">
        <i className="fas fa-2x fa-align-justify"></i>
      </div>
      <div className="user-friends">
        <input
          onChange={(e) => setInput(e.target.value)}
          value={input}
          type="text"
          placeholder="Search..."
        />
        <SearchResult />
        <Chats />
      </div>
    </form>
  );
};

export default Search;
